import { FlatList, Modal, Pressable, StyleSheet, Text, TextInput, TouchableOpacity, View } from 'react-native'
import React, { useEffect, useState } from 'react'

const src1 = () => {
  const [list, setList] = useState([])
  const [close, setClose] = useState(false)
  const [name, setName] = useState("")
  const [gender, setGender] = useState(0)
  const [id, setId] = useState(null)
  useEffect(() => {
    getList();
  }, [])
  const getList = () => {
    fetch("https://654ad5c25b38a59f28ee45ce.mockapi.io/job", {
      method: "GET"
    }).then(res => {
      return res.json()
    }).then(res => {
      console.log(res)
      if (res) {
        setList(res);
      }
    }).catch(err => {
      console.log(err)
    })
  }
  const remove = (item) => {
    fetch(`https://654ad5c25b38a59f28ee45ce.mockapi.io/job/${item.id}`, {
      method: "DELETE"
    })
      .then(res => {
        if (res.ok) {
          // Cập nhật trạng thái sau khi xóa thành công
          setList(prevList => prevList.filter(i => i.id !== item.id));
        } else {
          console.log("Xóa đéo được");
        }
      })
      .catch(err => {
        console.log(err);
      });
  }
  const save = (item) => {
    // Check if both name and gender are provided
    if (!name || !gender) {
      console.log("Nhập tên và giới tính  dùm");
      return;
    }

    // Convert gender to a numeric value (assuming 1 for Male, 0 for Female)
    const genderValue = gender.toLowerCase() === "male" ? 1 : 0;

    // Prepare data for the POST or PUT request based on whether id is available
    const newData = {
      name: name,
      gender: genderValue,
    };

    const apiEndpoint = id
      ? `https://654ad5c25b38a59f28ee45ce.mockapi.io/job/${id}`
      : "https://654ad5c25b38a59f28ee45ce.mockapi.io/job";

    const method = id ? "PUT" : "POST";

    // Send POST or PUT request to the API endpoint
    fetch(apiEndpoint, {
      method: method,
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newData),
    })
      .then((res) => res.json())
      .then((res) => {
        console.log(res);

        // If editing, update the local state with the edited data
        if (id) {
          setList((prevList) =>
            prevList.map((item) => (item.id === id ? res : item))
          );
        } else {
          // If adding new, update the local state with the new data
          setList((prevList) => [...prevList, res]);
        }

        // Close the modal after saving
        setClose(false);
        setName("");
        setGender(0);
        setId(null);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const showEdit = (item) => {
    setName(item.name)
    var genderValue = item.gender === 1 ? "Male" : "Female"
    setGender(genderValue)
    setId(item.id)
    setClose(true)
  }
  return (
    <View style={{ flex: 1 }}>
      <Modal
        visible={close}
      >
        <View style={{ flexDirection: 'row', justifyContent: 'space-around' }}>
          <Text style={{ fontSize: 30, textAlign: 'center', fontWeight: 'bold' }}>New</Text>
          <TouchableOpacity onPress={() => {
            setClose(false)

          }}
          >
            <Text style={{ color: "#DC143C", fontSize: 20 }}>Close</Text>
          </TouchableOpacity>
        </View>
        <View style={{ margin: 19 }}>
          <Text>Họ và Tên</Text>
          <TextInput
            style={{ margin: 10, height: 60, borderWidth: 1, borderColor: "gray" }}
            placeholderTextColor={"gray"}
            placeholder='Nhập tên'
            value={name}
            onChangeText={(text) => {
              setName(text)
            }}
          />
          <Text>Giới tính</Text>
          <TextInput
            style={{ margin: 10, height: 60, borderWidth: 1, borderColor: "gray" }}
            placeholderTextColor={"gray"}

            placeholder='Nhập giới tính (Male/Female)'
            value={gender}
            onChangeText={(text) => {
              setGender(text)
            }}
          />
        </View>
        <View style={{ justifyContent: 'center', alignItems: 'center' }}>
          <TouchableOpacity
            onPress={save} >

            <Text style={{ color: "#07f7f7", fontSize: 20 }}>Lưu</Text>
          </TouchableOpacity>
        </View>
      </Modal>
      <Text style={{ fontSize: 30, textAlign: 'center', fontWeight: 'bold' }}>Ten va gioi tinh</Text>
      <View style={{}}>
        <Pressable
          onPress={() => { setClose(true) }}
          style={{ height: "40px", width: "80px", backgroundColor: "blue" }}
        >
          <Text style={{ fontSize: 30, textAlign: 'center', fontWeight: 'bold' }}>New</Text>
        </Pressable>
      </View>
      <FlatList
        data={list}
        numColumns={1}
        renderItem={({ item }) => {
          return (
            <View style={{ flexDirection: 'row' }}>
              <View style={{ marginLeft: 18, }}>
                <Text style={{ fontSize: 20, fontWeight: '500' }}>Ten: {item.name}</Text>
                <Text style={{ fontSize: 20, fontWeight: '500' }}>Gioi Tinh: {item.gender == 1 ? "Male" : "Female"}</Text>
              </View>
              <View>
                <Pressable
                  onPress={() => { remove(item) }}
                  style={{ height: "35px", width: 40, backgroundColor: "red" }}
                >
                  <Text style={{ textAlign: 'center', marginTop: 6 }}>Delete</Text>
                </Pressable>
                <Pressable
                  onPress={() => { showEdit(item) }}
                  style={{ height: "35px", width: 40, backgroundColor: "blue" }}
                >
                  <Text style={{ textAlign: 'center', marginTop: 6 }}>Edit</Text>
                </Pressable>
              </View>
            </View>

          )
        }}

      />
    </View>
  )
}

export default src1

const styles = StyleSheet.create({})
