import { connect } from 'react-redux';
import { addOrUpdateItem, removeItem } from '../redux/action';
import { FlatList, Modal, Pressable, StyleSheet, Text, TextInput, View } from 'react-native'
import React, { useEffect, useState } from 'react'

const src1 = ({ addOrUpdateItem, removeItem }) => {

    const [list, setList] = useState([])
    const [close, setClose] = useState(false)
    const [name, setName] = useState("")
    const [gender, setGender] = useState("")
    const [id, setId] = useState(null)

    useEffect(() => {
        getList();
    }, [])

    const getList = () => {
        fetch('https://654de1d3cbc325355742011e.mockapi.io/jb', {
            method: 'GET'
        }).then(res => {
            return res.json()
        }).then(res => {
            console.log(res)
            if (res) {
                setList(res);
            }
        }).catch(err => {
            console.log(err)
        })
    }

    const save = (item) => {
        if (!name || !gender) {
            console.log("Nhap di");
            return;
        }
        const genderValue = gender.toLowerCase() === "male" ? 1 : 0;

        const newData = {
            name: name,
            gender: genderValue,
        };
        const apiEndpoint = id
            ? "https://654de1d3cbc325355742011e.mockapi.io/jb/${id}"
            : "https://654de1d3cbc325355742011e.mockapi.io/jb";

        const method = id ? "PUT" : "POST";

        fetch(apiEndpoint, {
            method: method,
            headers: {
                "Content-Type": "application/json",

            },
            body: JSON.stringify(newData),

        })
            .then((res) => res.json())
            .then((res) => {
                console.log(res);

                if (id) {
                    setList((preList) =>
                        preList.map((item) => (item.id === id ? res : item))
                    );
                } else {
                    setList((preList) => [...preList, res]);
                }
                setClose(false);
                setName("");
                setGender("");
                setId(null);

            })
            .catch((err) => {
                console.log(err);
            });
        addOrUpdateItem(res);
    };
    const remove = (item) => {
        fetch(`https://654de1d3cbc325355742011e.mockapi.io/jb/${item.id}`, {
            method: "DELETE"
        })
            .then(res => {
                if (res.ok) {
                    setList(preList => preList.filter(i => i.id !== item.id));

                } else {
                    console.log("Xoa eo dc");
                }
            })
            .catch(err => {
                console.log(err);
            });
        removeItem(item.id);
    };
    const showEdit = (item) => {
        setName(item.name)
        var gender = item.gender === 1 ? "Male" : "Female"
        setGender(genderValue)
        setId(item.id)
        setClose(true)
    }



    return (
        <View>
            
        </View>
    );
};

const mapStateToProps = (state) => ({
    list: state.list,
});

const mapDispatchToProps = {
    addOrUpdateItem,
    removeItem,
};

export default connect(mapStateToProps, mapDispatchToProps)(src1);
